android {
    compileSdkVersion 30
    buildToolsVersion "30.0.2"

    defaultConfig {
        applicationId "com.al.mond.oreo"
        minSdkVersion 23
        targetSdkVersion 30
        versionCode 1
        versionName "1.0"

        testInstrumentationRunner "androidx.test.runner.AndroidJUnitRunner"
    }


    flavorDimensions  "store","server"
    productFlavors {
        googlePlay {
            dimension "store"
            applicationIdSuffix ".google"
            resValue "string", "storeInfo", "OOOOOO"
        }
        oneStore {
            dimension "store"
            applicationIdSuffix ".onestore"
            resValue "string", "storeInfo", "XXXXXX"
        }
        dev {
            dimension "server"
            resValue "string", "server_url", "https://test.oreo.com/rest"
        }
        prod {
            dimension "server"
            resValue "string", "server_url", "https://oreo.com/rest"
        }
    }

    buildTypes {
        release {
            //ìŠ¤í† ì–´ ë°°í¬
            minifyEnabled true
            debuggable false
            proguardFiles getDefaultProguardFile('proguard-android-optimize.txt'), 'proguard-rules.pro'
        }

        inhouse {
            //ë‚´ë¶€ ë°°í¬
            initWith release
            debuggable true
        }

        debug {
            //ê°œë°œ
            minifyEnabled false
            debuggable true
        }
    }

    compileOptions {
        sourceCompatibility JavaVersion.VERSION_1_8
        targetCompatibility JavaVersion.VERSION_1_8
    }
    kotlinOptions {
        jvmTarget = '1.8'
    }
}

